sources.layout/_global.scss:
body {
    background: var(--color__primary);
    color: var(--color__text);
    font-family: var(--font);

    & > header.site-header,
    & > main.site-main,
    & > footer.site-footer {
        padding: 1.5rem;
        max-width: 1200px;
        margin: 0 auto;
    }
}

sources.layout/_header.scss:
header.site-header {
    background: var(--color__primary);
    display: grid;
    grid-template-columns: 100%;
    grid-template-rows: 50%;
    position: sticky;
    top: 0;

    @include breakpoint(large) {
        grid-template-columns: 30% 70%;
        grid-template-rows: 100%;
        align-items: end;
    }
}

sources.layout/_page.scss:
main.site-main {
    padding: 1.5rem;
    border-top: var(--border__body);
    border-bottom: var(--border__body);
    background: var(--color__primary--main);
    display: grid;
    justify-content: center;
    grid-template-rows: auto;
    grid-template-columns: 100%;

    @include breakpoint(medium) {
        grid-template-columns: 80%;
    }
}

sources.layout/_page--about.scss:
.about {
    // bio stuff here
    .about__content {
        display: grid;
        grid-template-columns: 100%;
        grid-gap: 2rem;

        @include breakpoint(medium) {
            grid-template-columns: 200px auto;

        }
    }
}

sources.layout/_page--post.scss:
.post {

