    }

    h2,
    h3,
    h4,
    h5,
    h6 {
        margin-bottom: 0;
        line-height: 1;
    }

    p { 
        &:nth-of-type(1) {
            &::first-letter {
                font-size: 4rem;
                font-weight: 800;
                margin-top: 0.25rem;
                padding-right: 0.5rem;
                padding-bottom: 0.25rem;
